package leetcode.lt75

object D2IsSubsequence extends App {

  def isSubsequence(s: String, t: String): Boolean = {
    if (s.isEmpty) true
    else {
      var idx = 0
      for (one <- t) {
        if (one == s(idx)) idx += 1
        if (idx == s.length) return true
      }
      false
    }
  }

  // println(isSubsequence("abc", "ahbgdc")) // t
  println(
    isSubsequence(
      "leeeeetcode",
      ""
    )
  ) // t
}
